pragma solidity ^0.5.0;

contract Supplier {
    //Model Supplier Details
    struct SupplierDetails {
        string tinNo;
        string supplierName;
        string supplierCountry;
        string location;
        string manufacturerName;
        string productName;
        string manufacturingCountry;
    }
    //store supplier
    //fetch supplier
    mapping(address => SupplierDetails) public suppliers;

    //store total no of products
    address[] public supplierAccounts;

    //set details of the product and producer
    function setSupplierDetails (
        address _address,
        string memory _tinNo, 
        string memory _supplierName, 
        string memory _supplierCountry,
        string memory _location,
        string memory _manufacturerName,
        string memory _productName,
        string memory _manufacturingCountry) public {
        SupplierDetails storage supplier = suppliers[_address];

        supplier.tinNo = _tinNo;
        supplier.supplierName = _supplierName;
        supplier.supplierCountry = _supplierCountry;
        supplier.location = _location;
        supplier.manufacturerName = _manufacturerName;
        supplier.productName = _productName;
        supplier.manufacturingCountry = _manufacturingCountry;

        supplierAccounts.push(_address)-1;
    }
    function countSuppliers() public view  returns (uint) {
        return supplierAccounts.length;
    }

    function getSuppliers() public view  returns (address[] memory) {
        return supplierAccounts;
    }

    function storeTransactionHashForSupplier() public view  returns (address) {
    }
}
